
public class Nestedloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=4;i++)
{
	System.out.println(i);
	for(int j=1;i<=3;j--)
	{
	System.out.println(j);
	for(int k=1;k<=2;k--)
	{
		System.out.println(k);
	}
	//System.out.println(10);
	}

}
	}
}


